import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Carousel from '../components/Carousel';
import { fetchProducts } from '../services/api';
import { useAuthStore } from '../store/useAuthStore';

const Home = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const user = useAuthStore((state) => state.user);

  useEffect(() => {
    const getProducts = async () => {
      try {
        setLoading(true);
        const products = await fetchProducts();
        setFeaturedProducts(products.slice(0, 6));
        setLoading(false);
      } catch (err) {
        console.error('Failed to fetch products', err);
        setLoading(false);
      }
    };
    getProducts();
  }, []);

  return (
    <div className="min-h-screen bg-white text-gray-800">
      {/* Hero Section */}
      <section className="relative h-screen bg-gradient-to-r from-secondary to-primary overflow-hidden">
        <img
          src="https://images.pexels.com/photos/2983464/pexels-photo-2983464.jpeg?auto=compress&cs=tinysrgb&h=800"
          alt="Hero Background"
          className="absolute w-full h-full object-cover opacity-40"
        />
        <div className="absolute inset-0 flex flex-col items-center justify-center px-4 text-center z-10">
          <h1 className="text-5xl md:text-7xl font-extrabold text-white drop-shadow mb-6">
            <span className="text-accent">Step Into</span> Style âœ¨
          </h1>
          <p className="text-2xl md:text-3xl text-white opacity-90 mb-10 max-w-3xl">
            Handpicked fashion, unbeatable offers, and trends that make a statement.
          </p>
          <div className="space-x-4">
            <Link to="/products" className="btn-primary text-lg px-8 py-3 rounded-full hover:scale-110 transition-transform shadow-lg">
              ðŸ”¥ Explore Collection
            </Link>
            {user === null && (
              <Link
                to="/signup"
                className="border-2 border-white text-white px-8 py-3 rounded-full hover:bg-white hover:text-secondary transition-all shadow-md"
              >
                Sign Up
              </Link>
            )}
          </div>
        </div>
      </section>

      {/* Rest of your sections remain the same */}

      {/* Features Section */}
      {/* Carousel Section */}
      {/* Category Grid */}
      {/* Popular Products */}
      {/* Newsletter */}
    </div>
  );
};

export default Home;
