import React from 'react';
import { Link } from 'react-router-dom';
import { useAuthStore } from '../store/useAuthStore';
import { useCartStore } from '../store/useCartStore';

const Navbar = () => {
  const cartItemsCount = useCartStore(state => state.cart.length);
  const { user, logout } = useAuthStore();

  return (
    <nav className="bg-white text-gray-800 shadow-md fixed w-full z-10">
      <div className="container mx-auto px-4 py-4 flex justify-between items-center">
        <Link to="/" className="text-3xl font-bold text-primary tracking-wide">
          SHOPIFY
        </Link>

        <div className="hidden md:flex items-center space-x-8 font-medium">
          <Link to="/" className="relative text-gray-700 hover:text-primary transition duration-200 after:absolute after:content-[''] after:block after:h-[2px] after:w-0 after:bg-primary after:transition-all after:duration-300 hover:after:w-full">
            Home
          </Link>

          {user ? (
            <>
              <Link to="/cart" className="relative text-gray-700 hover:text-primary transition duration-200">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 inline" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z" />
                </svg>
                {cartItemsCount > 0 && (
                  <span className="absolute -top-2 -right-2 bg-primary text-white text-xs font-semibold rounded-full h-5 w-5 flex items-center justify-center shadow-md">
                    {cartItemsCount}
                  </span>
                )}
              </Link>

              <button
                onClick={logout}
                className="bg-primary text-white px-4 py-2 rounded-md hover:bg-primary-dark transition-all duration-200 shadow-sm"
              >
                Logout
              </button>
            </>
          ) : (
            <Link
              to="/login"
              className="bg-primary text-white px-4 py-2 rounded-md hover:bg-primary-dark transition-all duration-200 shadow-sm"
            >
              Login
            </Link>
          )}
        </div>

        {/* Mobile Menu Button */}
        <button className="md:hidden text-gray-800 focus:outline-none">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-7 w-7" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
          </svg>
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
