import React, { useState, useEffect } from 'react';
import Carousel from '../components/Carousel';
import ProductCard from '../components/ProductCard';
import { fetchProducts } from '../services/api';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getProducts = async () => {
      try {
        setLoading(true);
        const data = await fetchProducts();
        setProducts(data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch products');
        setLoading(false);
      }
    };

    getProducts();
  }, []);

  const trendingProducts = products.filter(
    (product) => product.category !== 'Clothing' && product.category !== 'Electronics'
  );
  const clothingProducts = products.filter((product) => product.category === 'Clothing');
  const electronicsProducts = products.filter((product) => product.category === 'Electronics');

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-white">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-b-4 border-primary"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-white">
        <div className="text-center">
          <p className="text-red-600 text-xl font-medium mb-4">{error}</p>
          <button
            onClick={() => window.location.reload()}
            className="bg-primary text-white px-6 py-2 rounded-lg hover:bg-primary-dark transition"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white min-h-screen">
      <Carousel />

      <div className="container mx-auto px-4 py-16">
        {/* Trending */}
        <section className="mb-20">
          <h2 className="text-3xl font-bold text-center text-primary mb-10">
            ðŸ”¥ Trending Products
          </h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
            {trendingProducts.map((product) => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>
        </section>

        {/* Clothing */}
        <section className="mb-20">
          <h2 className="text-3xl font-bold text-center text-primary mb-10">
            ðŸ‘— Clothing Collection
          </h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
            {clothingProducts.map((product) => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>
        </section>

        {/* Electronics */}
        <section>
          <h2 className="text-3xl font-bold text-center text-primary mb-10">
            ðŸ’» Electronics & Gadgets
          </h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
            {electronicsProducts.map((product) => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>
        </section>
      </div>
    </div>
  );
};

export default Products;
